        private Texture2D TheTreasureShowPicture(int i)
        {
            Texture2D P = this.PictureNull;
            int n = 1;
            foreach (Treasure t in this.ShowingPerson.Treasures)
            {
                if (n == i)
                {
                  P = t.Picture; 
                  break;
                }
                n++;
            }            
            return P;
        }       
         
        private Rectangle TheTreasureShowDisplayPosition(int i)
        {
            Rectangle D = this.NullDisplayPosition;
            int H = 0;
            int V = 0;
            int XN = 0;
            int YN = 0;
            XN = this.TheTreasureShowClient.Width;
            YN = this.TheTreasureShowClient.Height;
            int EveryPageMaxNumber = 0;
            EveryPageMaxNumber = HNumber * VNumber;
            int P = 0;
            for (int p = 0; p < MaxTreasureShowPage; p++)
            {
                if (i > p*EveryPageMaxNumber && i <= (p + 1) * EveryPageMaxNumber)
                {
                    P = p + 1;
                    break;
                }
            }
            if (P == TheTreasurePageNumber)
            {
                int I = 0;
                I = i - EveryPageMaxNumber*(P-1);
                for (int n = 0; n < VNumber; n++)
                {
                    if (I <= (n + 1) * HNumber)
                    {
                        H = I - n * HNumber - 1;
                        V = n;
                        D = new Rectangle(H * this.HSpace + (H*XN) + this.TheTreasureShowClient.X + this.DisplayOffset.X, V * this.VSpace + (V*YN) + this.TheTreasureShowClient.Y + this.DisplayOffset.Y, this.TheTreasureShowClient.Width, this.TheTreasureShowClient.Height);
                        break;
                    }
                }
            }
            return D;
        }
